import React, { useState , useEffect} from 'react'
import {connect} from 'react-redux';
import './todo.css';
import { getData, putData, deleteData, postData } from '../api';
import TodoList from './TodoList';


function Todo(props) {
    console.log(props);
    const [newTodo,setNewtodo]= useState('');
    const [edit,setEdit]= useState(false);

    const [products, setProducts] = useState([]);

    useEffect(() => {
        getAllProducts();
        // JSON.parse(window.localStorage.getItem('tododata'))
    }, [])
    async function getAllProducts() {
        const response = await getData();
         window.localStorage.setItem('tododata', JSON.stringify(response.data.todos));
         let data = JSON.parse(window.localStorage.getItem('tododata'));
         console.log(data);
        setProducts(data);
    }
    async function addProduct(product) {
        let data = {
            name: product.name,
            price: product.price,
            category: product.category
        }
        if (edit){
            await putData(product.id, data);
        }
        else{
            await postData(data);
        }
        // getAllProducts();


    }
    function editProduct(value) {
        setEdit(true);

    }
    async function deleteProduct(id) {
       let res =  await deleteData(id);
       console.log(res);
        window.localStorage.removeItem(res.data)
    }
  return (
    <div>
        <h1>Todolist2</h1>
        <input type='text' onChange={(e)=> setNewtodo(e.target.value)}/>
        <button onClick={()=>props.dispatch({type:'ADD_TODo',payload:newTodo})} >Add todo</button>
        <TodoList products={products} editProduct={editProduct} deleteProduct={deleteProduct}/>
    </div>
  )
}


export default connect(function(store){return store})(Todo)
