import React, { useState , useEffect} from 'react'
import {connect} from 'react-redux';
import './todo.css';
import { getData, putData, deleteData, postData } from '../api';
import TodoList from './TodoList';


function Todo(props) {
    console.log(props);
    const [newTodo,setNewtodo]= useState('');
    const [edit,setEdit]= useState(false);

    const [products, setProducts] = useState([]);

    useEffect(() => {
        getAllProducts();
    }, [])
    async function getAllProducts() {
        const response = await getData();
        console.log(response)
        setProducts(response.data);
    }
    async function addProduct(newtodo) {
        console.log(newTodo);
        let data = {
            name: newtodo,
            completed:false,
            userId:Math.random(50)
        }
        if (edit){
            await putData(newTodo.id);
        }
        else{
            await postData(data);
        }
        // getAllProducts();


    }
    function editProduct(value) {
        setEdit(true);
        console.log(value);
        setNewtodo(value.todo)

    }
    async function deleteProduct(id) {
       let res =  await deleteData(id);
       console.log(res);
        // getAllProducts();
    }
  return (
    <div>
        <h1>Todolist2</h1>
        <input type='text' value={newTodo} onChange={(e)=> setNewtodo(e.target.value)}/>
        <button onClick={()=>addProduct(newTodo)} >Add todo</button>
        <TodoList products={products} editProduct={editProduct} deleteProduct={deleteProduct}/>
    </div>
  )
}


export default connect(function(store){return store})(Todo)
