import React, { useState , useEffect} from 'react'
import {connect} from 'react-redux';
import './todo.css';
import { getData, putData, deleteData, postData } from '../api';
import TodoList from './TodoList';


function Todo(props) {
    console.log(props);
    // const [newTodo,setNewtodo]= useState('');
    const [edit,setEdit]= useState(false);

    const [products, setProducts] = useState([]);
    const [intialState, setInitialstate]= useState({
        todo: "",
        completed: false,
        userId: ''
    })

    useEffect(() => {
        getAllProducts();
    }, [])
    async function getAllProducts() {
        const response = await getData();
        console.log(response)
        setProducts(response.data);
    }
    async function addProduct(intialState) {
        console.log(intialState);
        console.log(edit);
        let data = {
            todo: intialState.todo,
            completed:false,
            userId:5,
        }
        if (edit){
            await putData(intialState.id, data);
        }
        else{
         await postData(intialState);

        }
        // getAllProducts();


    }
    function editProduct(value) {
        setEdit(true);
        console.log(value);
        // setNewtodo(value.todo);
        setInitialstate(value)

    }
    async function deleteProduct(id) {
       let res =  await deleteData(id);
       console.log(res);
        // getAllProducts();
    }
  return (
    <div>
        <h1>Todolist2</h1>
        <input type='text' name='todo' value={intialState.todo} onChange={(e)=> setInitialstate({...intialState,[e.target.name]:e.target.value})}/>
        <button onClick={()=>addProduct(intialState)} >Add todo</button>
        <TodoList products={products} editProduct={editProduct} deleteProduct={deleteProduct}/>
    </div>
  )
}


export default connect(function(store){return store})(Todo)
