const intialState = {
        id: '',
        todo: "",
        completed: false,
        userId: 5

}
function todoreducer(state= intialState, action){
    if(action.type === 'ADD_TODo'){
        const newTodo = { id: state.nextId, title: action.payload };
        return {
          ...state,
          todos: [...state.todos, newTodo],
          nextId: state.nextId + 1,
        };
    } else {
        return state
    }
}
export default todoreducer;
