import React, { useState , useEffect} from 'react'
import './todo.css';
import {postData } from '../api';
import { useSelector, useDispatch } from 'react-redux';
import TodoList from './TodoList';
import { fetchData } from '../reducers/actions';

function Todo(props) {
    console.log(props);
    const dispatch = useDispatch();
    const dataState = useSelector(state => state.todoReducer);
    console.log(dataState);
    const [edit,setEdit]= useState(false);
    // const [products, setProducts] = useState([]);
    const [intialState, setInitialstate]= useState('')
    useEffect(() => {
        dispatch(fetchData());
    }, [ dispatch]);

    if (dataState.loading) {
        return <h2>Loading...</h2>;
    }

    if (dataState.error) {
        return <h2>{dataState.error}</h2>;
    }

    // async function addProduct(intialState) {
    //     console.log(intialState, edit);

    //     if (edit){
    //         console.log('editblock');
    //         dispatch(editProduct(intialState.id));

    //     }
    //     else{
    //      let postres = await postData({...intialState,userId:(Math.ceil(Math.random(20,80)*100))});
    //      console.log(postres);

    //     }


    // }


  return (
    <div>
        <h1>Todolist</h1>
        <input type='text' name='todo' value={intialState.todo} onChange={(e)=> setInitialstate(e.target.value)}/>
        <button >Add todo</button>
        <TodoList data={dataState.data}/>
    </div>
  )
}


export default Todo
