import React, { useState , useEffect} from 'react'
import {connect} from 'react-redux';
import './todo.css';
import { getData, putData, deleteData, postData } from '../api';
import TodoList from './TodoList';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

function Todo(props) {
    const [edit,setEdit]= useState(false);

    const [products, setProducts] = useState([]);
    const [intialState, setInitialstate]= useState({
        todo: "",
        completed: false,
        userId: ''
    })

    useEffect(() => {
        getAllProducts();
    }, [])
    async function getAllProducts() {
        const response = await getData();
        setProducts(response.data.todos);
    }
    async function addProduct(intialState) {
        let data = {
            todo: intialState.todo,
            completed:false,
            userId:5,
        }
        if (edit){
           let putres =  await putData(intialState.id, data);
           console.log(putres);

        }
        else{
         let postres = await postData({...intialState,userId:(Math.ceil(Math.random(20,80)*100))});
         console.log(postres);

        }
        // getAllProducts();


    }
    function editProduct(value) {
        setEdit(true);
        setInitialstate(value)

    }
    async function deleteProduct(id) {
       let res =  await deleteData(id);
       console.log(res);
    }
  return (
    <div>
        <h1>Todolist2</h1>
        <Box
      component="form"
      sx={{
        '& > :not(style)': { mb: 2, width: '45ch' },
      }}
      noValidate
      autoComplete="off"
    >
      <TextField id="outlined-basic"
       value={intialState.todo}
        onChange={(e)=> setInitialstate({...intialState,[e.target.name]:e.target.value})}
       label="Add Todo" variant="outlined" />
    </Box>
        {/* <input type='text' name='todo' value={intialState.todo} onChange={(e)=> setInitialstate({...intialState,[e.target.name]:e.target.value})}/> */}
        <button onClick={()=>addProduct(intialState)} >Add todo</button>
        <TodoList products={products} editProduct={editProduct} deleteProduct={deleteProduct}/>
    </div>
  )
}


export default connect(function(store){return store})(Todo)
