import TodoList from './TodoList';
import { addProduct, editProduct, fetchData } from '../reducers/actions';


function Todo(props) {
    console.log(props);
    const dispatch = useDispatch();
    const dataState = useSelector(state => state.todoReducer);
    console.log(dataState);
    // const [products, setProducts] = useState([]);
    const [intialState, setInitialstate]= useState({todo:'', completed:'', userId: ''})
    useEffect(() => {
        dispatch(fetchData());
    }, [ dispatch]);

    if (dataState.loading) {
        return <h2>Loading...</h2>;
    }

    if (dataState.error) {
        return <h2>{dataState.error}</h2>;
    }
    const addProduct=(todo)=>{
