const intialState = {
   todo: [{
    todo: '',
    completed: false,
    userId:"0"
   }]

}
function todoreducer(state= intialState, action){
    if(action.type === 'ADD_TODo'){
        const newTodo = { todo: action.payload, completed:false, userId:state.userId+1 };
        return {
          ...state,
          todo: [...state.todo, newTodo],

        };
    } 
}
export default todoreducer;
