import React, { useState , useEffect} from 'react'
import {connect} from 'react-redux';
import './todo.css';
import { getData } from '../api';
import TodoList from './TodoList';


function Todo(props) {
    console.log(props);
    const [newTodo,setNewtodo]= useState('');
    const [products, setProducts] = useState([]);

    useEffect(() => {
        getAllProducts();
    }, [])
    async function getAllProducts() {
        const response = await getData();
        setProducts(response.data);
    }
  return (
    <div>
        <h1>Todolist2</h1>
        <input type='text' onChange={(e)=> setNewtodo(e.target.value)}/>
        <button onClick={()=>props.dispatch({type:'ADD_TODo',payload:newTodo})} >Add todo</button>
        <TodoList products={products}/>

        {
            props.todos.map((todo)=>{
                console.log(todo);
                return (
                <div className='todo'>
                   {/* <div>{todo.title}</div>
                   <div><button className='btn btn-primary'>Edit</button></div>
                   <div><button> delete</button></div> */}
                    </div>
                )
            })
        }
    </div>
  )
}


export default connect(function(store){return store})(Todo)
